package com.example.hw4_3mon.CONTINENT;

import android.os.Bundle;

import androidx.annotation.NonNull;
import androidx.annotation.Nullable;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.RecyclerView;

import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import com.example.hw4_3mon.COUNTRY.CountryFragment;
import com.example.hw4_3mon.OnClick;
import com.example.hw4_3mon.R;
import com.example.hw4_3mon.databinding.FragmentFirstBinding;

import java.util.ArrayList;

public class FirstFragment extends Fragment implements OnClick {

    private RecyclerView rvContinet;
    private FragmentFirstBinding binding;
    private ArrayList<ContinentModel> continentList = new ArrayList<>();

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container,
                             Bundle savedInstanceState) {
        binding = FragmentFirstBinding.inflate(getLayoutInflater());
        return binding.getRoot();
    }

    @Override
    public void onViewCreated(@NonNull View view, @Nullable Bundle savedInstanceState) {
        super.onViewCreated(view, savedInstanceState);
        rvContinet = requireActivity().findViewById(R.id.rv_continent);
        loadData();
        CotinentAdapter adapter = new CotinentAdapter(continentList, this);
        binding.rvContinent.setAdapter(adapter);
    }

    private void loadData() {
        continentList.add(new ContinentModel("Европа", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTQPQQSeEpHWJSVfilBv6apAKg5eXxi-_qfZQ&usqp=CAU"));
        continentList.add(new ContinentModel("Африка", "data:image/png;base64,"));
        continentList.add(new ContinentModel("Северная Америка", "data:image/png;base64,"));
        continentList.add(new ContinentModel("Евразия", "https://flagi-1.ru/wp-content/uploads/2016/10/ax-800x523.png"));
        continentList.add(new ContinentModel("Южная Америка", "data:image/png;base64,"));
    }

    public void onClick(int position){
        Bundle bundle = new Bundle();
        bundle.putInt("key", position);

        CountryFragment countryFragment = new CountryFragment();
        countryFragment.setArguments(bundle);
        requireActivity().getSupportFragmentManager().beginTransaction().replace(R.id.fragment_container, countryFragment).addToBackStack(null).commit();
    }
}